  Expect&lt;Equal&lt;If&lt;true, &quot;a&quot;, &quot;b&quot;&gt;, &quot;a&quot;&gt;&gt;,
  Expect&lt;Equal&lt;If&lt;false, &quot;a&quot;, 2&gt;, 2&gt;&gt;
];

